glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec2 a_uv0;
out vec2 v_uv0;
void main () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * pos;
  v_uv0 = a_uv0;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
in vec2 v_uv0;
uniform sampler2D texture;
uniform sampler2D lutTexture;
uniform sampler2D lightTexture;
uniform NightEffect {
  float nightMix;
};
vec4 lerp(vec4 a, vec4 b, float w){
  return a * (1.0 - w) + b * w;
}
vec4 lut(vec4 basics){
  if(nightMix < 0.01) return basics;
  float blueColor = basics.b * 63.0;
  vec2 quad1;
  quad1.y = floor(floor(blueColor) / 8.0);
  quad1.x = floor(blueColor) - (quad1.y * 8.0);
  vec2 quad2;
  quad2.y = floor(ceil(blueColor) / 8.0);
  quad2.x = ceil(blueColor) - (quad2.y * 8.0);
  vec2 texPos1;
  texPos1.y = (quad1.x * 0.125) + 0.5/512.0 + ((0.125 - 1.0/512.0) * basics.r);
  texPos1.x = (quad1.y * 0.125) + 0.5/512.0 + ((0.125 - 1.0/512.0) * basics.g);
  vec2 texPos2;
  texPos2.y = (quad2.x * 0.125) + 0.5/512.0 + ((0.125 - 1.0/512.0) * basics.r);
  texPos2.x = (quad2.y * 0.125) + 0.5/512.0 + ((0.125 - 1.0/512.0) * basics.g);
  vec4 newColor1 = texture2D(lutTexture, texPos1);
  vec4 newColor2 = texture2D(lutTexture, texPos2);
  vec4 newColor = mix(newColor1, newColor2, fract(blueColor));
  newColor.a = basics.a;
  return mix(basics, newColor, nightMix);
}
void main () {
  vec4 basics = texture2D(texture, v_uv0.xy);
  vec4 night = lut(basics);
  vec4 light = texture2D(lightTexture, v_uv0.xy);
  night = lerp(night, basics, light.a);
  float dis = distance(v_uv0, vec2(0.5, 0.6));
  dis = mix(-0.2, 0.8, clamp(dis, 0.4, 0.8) - 0.4) + 0.2;
  night = mix(night, vec4(0.0), dis * nightMix * 2.0);
  gl_FragColor = night;
}

