glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
in vec2 a_uv0;
out vec2 v_uv0;
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec4 v_color;
in vec2 v_uv0;
uniform sampler2D texture;
uniform Texture{
  vec4 uv;
  vec4 u;
  vec4 v;
  vec2 scale;
  float speed;
};
float linear(float x0, float x1, float y0, float y1, float input0){
  return (y1 - y0) * (input0 - x0) / (x1 - x0) + y0;
}
void main () {
  vec2 fixUv = v_uv0.xy;
  fixUv.x = linear(uv[0], uv[1], 0.0, 1.0, fixUv.x);
  fixUv.y = linear(uv[2], uv[3], 0.0, 1.0, fixUv.y);
  if(fixUv.x < u[0]){
    fixUv.x = linear(0.0, u[0], 0.0, u[2], fixUv.x);
  }else if(fixUv.x < u[1]){
    fixUv.x = linear(u[0], u[1], u[2], u[3], fixUv.x);
  }else{
    fixUv.x = linear(u[1], 1.0, u[3], 1.0, fixUv.x);
  }
  if(fixUv.y < v[0]){
    fixUv.y = linear(0.0, v[0], 0.0, v[2], fixUv.y);
  }else if(fixUv.y < v[1]){
    fixUv.y = linear(v[0], v[1], v[2], v[3], fixUv.y);
  }else{
    fixUv.y = linear(v[1], 1.0, v[3], 1.0, fixUv.y);
  }
  fixUv = mod((fixUv + vec2( -cc_time.x * speed, cc_time.x * speed ) * 0.1) / scale, 1.0);
  gl_FragColor = texture2D(texture,fixUv) * v_color;
}

