glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
uniform InputData{
  vec4 stripeColor;
  vec4 wordColor;
  vec4 lineColor;
  vec2 nodeSize;
  float stripeWidth;
  float stripeHeight;
  float stripeSpacing;
  float stripeMovingSpeed;
  float stripeRatio;
  float lineHeight;
  float blinkSpeed;
  float timer;
};
void main () {
  vec4 o = wordColor;
  #if USE_TEXTURE
    vec2 uv = v_uv0 - 0.5;
    float nStripeWidth = stripeWidth / nodeSize.x;
    float nStripeHeight = stripeHeight / nodeSize.y;
    float nStripeSpacing = stripeSpacing / nodeSize.x;
    float nStripeMovingSpeed = stripeMovingSpeed / nodeSize.x;
    float nLineHeight = lineHeight / nodeSize.y;
    float nStripeRatio = stripeRatio * nodeSize.y / nodeSize.x;
    float movingDis = nStripeMovingSpeed * timer;
    float topStripeMark = step(mod(v_uv0.x + movingDis + uv.y * nStripeRatio, nStripeWidth + nStripeSpacing), nStripeWidth) * step(uv.y, -(0.5 - nStripeHeight));
    float bottomStripeMark = step(mod(v_uv0.x - movingDis + uv.y * nStripeRatio, nStripeWidth + nStripeSpacing), nStripeWidth) * step(0.5 - nStripeHeight, uv.y);
    float wave = sin(timer * 6.28 * blinkSpeed);
    float outOrIn = step(0.0, wave);
    float outLineMark = step(uv.y, -(0.5 - nStripeHeight - nLineHeight)) - step(uv.y, -(0.5 - nStripeHeight)) + step(0.5 - nStripeHeight - nLineHeight, uv.y) - step(0.5 - nStripeHeight, uv.y);
    outLineMark *= (1.0 - outOrIn);
    float inLineMark = step(uv.y, -(0.5 - nStripeHeight - nLineHeight * 2.0)) - step(uv.y, -(0.5 - nStripeHeight - nLineHeight)) + step(0.5 - nStripeHeight - nLineHeight * 2.0, uv.y) - step(0.5 - nStripeHeight - nLineHeight, uv.y);
    inLineMark *= outOrIn;
    vec4 resultstripeColor = stripeColor * (topStripeMark + bottomStripeMark);
    vec4 resultLineColor = lineColor * (outLineMark + inLineMark);
    float orgA = texture(texture, v_uv0).a;
    o.a = (orgA * outOrIn + (1.0 - orgA) * (1.0 - outOrIn)) * step(abs(uv.y), 0.5 - nStripeHeight - nLineHeight * 2.0);
    o = mix(resultstripeColor + resultLineColor, o, o.a);
    o.a *= abs(wave);
  #endif
  ALPHA_TEST(o);
  gl_FragColor = o;
}

