glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
#if USE_MASK
uniform sampler2D maskTex;
uniform USE_MASK{
  vec2 scaleUV;
};
#endif
#if FIX_PACKAGE_UV
uniform FIX_PACKAGE_UV{
  vec4 fixUV;
  float fixRotate;
};
#endif
void main () {
  vec4 o = vec4(1, 1, 1, 1);
  #if USE_TEXTURE
    o = texture(texture, v_uv0);
  #endif
  #if USE_MASK
    vec2 uv = v_uv0;
    #if FIX_PACKAGE_UV
      uv.x = (v_uv0.x - fixUV.x) / (fixUV.z - fixUV.x);
      uv.y = (v_uv0.y - fixUV.y) / (fixUV.w - fixUV.y);
      float temp = uv.x;
      uv.x = uv.x + (uv.y - uv.x) * fixRotate;
      uv.y = uv.y + ((1.0 - temp) - uv.y) * fixRotate;
    #endif
    vec2 min = (vec2(1, 1) - scaleUV) * 0.5;
    vec2 max = vec2(1, 1) - min;
    vec4 mask = texture(maskTex, (uv - min) / scaleUV);
    o.a *= step(min.x, uv.x) * step(uv.x, max.x) * step(min.y, uv.y) * step(uv.y, max.y) * mask.a;
  #endif
  o *= v_color;
  gl_FragColor = o.rgba;
}

