#if USE_TEXTURE
varying vec2 v_uv0;
uniform sampler2D texture;
#endif
#if USE_MASK
uniform sampler2D maskTex;
uniform vec2 scaleUV;
#endif
#if FIX_PACKAGE_UV
uniform vec4 fixUV;
uniform float fixRotate;
#endif
void main () {
  vec4 o = vec4(1, 1, 1, 1);
  #if USE_TEXTURE
    o = texture2D(texture, v_uv0);
  #endif
  #if USE_MASK
    vec2 uv = v_uv0;
    #if FIX_PACKAGE_UV
      uv.x = (v_uv0.x - fixUV.x) / (fixUV.z - fixUV.x);
      uv.y = (v_uv0.y - fixUV.y) / (fixUV.w - fixUV.y);
      float temp = uv.x;
      uv.x = uv.x + (uv.y - uv.x) * fixRotate;
      uv.y = uv.y + ((1.0 - temp) - uv.y) * fixRotate;
    #endif
    vec2 min = (vec2(1, 1) - scaleUV) * 0.5;
    vec2 max = vec2(1, 1) - min;
    vec4 mask = texture2D(maskTex, (uv - min) / scaleUV);
    o.a *= step(min.x, uv.x) * step(uv.x, max.x) * step(min.y, uv.y) * step(uv.y, max.y) * mask.a;
  #endif
  o *= v_color;
  gl_FragColor = o.rgba;
}
